def factorial(n):
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

def binomial_coeff(n, k):
    if (k > n - k):
        k = n - k
    res = 1
    for x in range(k):
        res = res * (n - x)
        res = res // (x + 1)
    return res

def find_Bn(n):
    if (n & 1): # nが奇数なら完全なバイナリツリーは0
        return 0
    c_n = binomial_coeff(2*n, n)
    return c_n // (n + 1)

# テスト
print(find_Bn(2))
print(find_Bn(4))
print(find_Bn(6))
```

